a1 = randi ([0 42], 1, 7);a2 = randi ([0 42], 1, 4);c = conv (a1, a2);diff = length (c) - length (a2);a2 = [a2, zeros(1, length(a1)-1)];C = fft (c);A2 = fft (a2);expected_A1 = C ./ A2;expected_a1 = ifft (expected_A1);expected_a1 = expected_a1(1:length(a1));for i = 1:length(expected_a1)  expected_a1(i) = int64 (expected_a1(i));endif nnz (expected_a1 - a1) == 0  disp ('all good');enddisp (a1);disp (expected_a1);